import React, { useState, useEffect } from 'react';
import Sidebar from '../components/Sidebar';
import axios from 'axios';

const Patients = () => {
  const [patients, setPatients] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    fetchPatients();
  }, []);

  const fetchPatients = async () => {
    try {
      const response = await axios.get('http://localhost:5000/patients');
      setPatients(response.data);
    } catch (error) {
      console.error('Error fetching patients:', error);
    }
  };

  const filteredPatients = patients.filter((patient) => {
    const patientName = patient['Patient Name'] ? patient['Patient Name'].toLowerCase() : '';
    const patientID = patient['Patient ID'] ? patient['Patient ID'].toLowerCase() : '';
    const lowerSearchTerm = searchTerm.toLowerCase();
    return patientName.includes(lowerSearchTerm) || patientID.includes(lowerSearchTerm);
  });

  return (
    <div style={{ display: 'flex' }}>
      <Sidebar />
      <main
        style={{
          flexGrow: 1,
          padding: '20px',
          marginTop: 64,
          boxSizing: 'border-box',
        }}
      >
        <h1>Patient Details</h1>
        <input
          type="text"
          placeholder="Search by Patient ID or Name"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{
            padding: '8px',
            width: '100%',
            marginBottom: '20px',
            borderRadius: '4px',
            border: '1px solid #ddd',
          }}
        />
        <table style={{ width: '100%', borderCollapse: 'collapse' }}>
          <thead>
            <tr>
              <th style={{ border: '1px solid #ddd', padding: '8px' }}>Patient ID</th>
              <th style={{ border: '1px solid #ddd', padding: '8px' }}>Name</th>
              <th style={{ border: '1px solid #ddd', padding: '8px' }}>Age</th>
              <th style={{ border: '1px solid #ddd', padding: '8px' }}>Condition</th>
              <th style={{ border: '1px solid #ddd', padding: '8px' }}>ESI Level</th>
            </tr>
          </thead>
          <tbody>
            {filteredPatients.map((patient, index) => (
              <tr key={index}>
                <td style={{ border: '1px solid #ddd', padding: '8px' }}>{patient['Patient ID']}</td>
                <td style={{ border: '1px solid #ddd', padding: '8px' }}>{patient['Patient Name']}</td>
                <td style={{ border: '1px solid #ddd', padding: '8px' }}>{patient.Age}</td>
                <td style={{ border: '1px solid #ddd', padding: '8px' }}>{patient['Emergency Type']}</td>
                <td style={{ border: '1px solid #ddd', padding: '8px' }}>{patient['ESI Level']}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </main>
    </div>
  );
};

export default Patients;
