import React, { useState } from 'react';
import { TextField, Button, Grid, Typography, Box } from '@mui/material';
import axios from 'axios';

const AddPatient = () => {
  const [formData, setFormData] = useState({
    'Patient ID': '',
    'Patient Name': '',
    Sex: '',
    'Emergency Type': '',
    'ESI Level': '',
    'Heart Rate': '',
    'Blood Pressure': '',
    'Respiratory Rate': '',
    'Oxygen Levels (%)': '',
    'Consciousness Score (GCS)': '',
    'Signs of Distress': '',
    'Signs of Bleeding/Shock': '',
    'Potential for Deterioration': '',
    Phone: '',
    'Marital Status': '',
    Address: '',
    Children: '',
    Spouse: '',
    'Insurance Status': '',
    'Insurance Provider': '',
    Smoker: '',
    'Non-Alcoholic': '',
    'Pain Level (1-10)': '',
    'Mobility Status': '',
    'Cognitive State': '',
    Age: '',
    'Temperature (째F)': '',
    'Recent Hospital Visits (Past 6 Months)': '',
    'Patient Check-In Time': '',
    'Insurance Category': '',
  });

  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:5000/add-patient', formData);
      setSuccessMessage(response.data.message);
      setErrorMessage('');
      setFormData({
        'Patient ID': '',
        'Patient Name': '',
        Sex: '',
        'Emergency Type': '',
        'ESI Level': '',
        'Heart Rate': '',
        'Blood Pressure': '',
        'Respiratory Rate': '',
        'Oxygen Levels (%)': '',
        'Consciousness Score (GCS)': '',
        'Signs of Distress': '',
        'Signs of Bleeding/Shock': '',
        'Potential for Deterioration': '',
        Phone: '',
        'Marital Status': '',
        Address: '',
        Children: '',
        Spouse: '',
        'Insurance Status': '',
        'Insurance Provider': '',
        Smoker: '',
        'Non-Alcoholic': '',
        'Pain Level (1-10)': '',
        'Mobility Status': '',
        'Cognitive State': '',
        Age: '',
        'Temperature (째F)': '',
        'Recent Hospital Visits (Past 6 Months)': '',
        'Patient Check-In Time': '',
        'Insurance Category': '',
      });
    } catch (error) {
      setErrorMessage('Error adding patient. Please try again.');
      setSuccessMessage('');
    }
  };

  return (
    <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', mt: 8 }}>
      <Typography variant="h4" gutterBottom>
        Add Patient
      </Typography>

      {successMessage && <Typography color="green">{successMessage}</Typography>}
      {errorMessage && <Typography color="red">{errorMessage}</Typography>}

      <form onSubmit={handleSubmit} style={{ width: '80%' }}>
        <Grid container spacing={2}>
          {Object.keys(formData).map((field) => (
            <Grid item xs={12} sm={6} key={field}>
              <TextField
                label={field}
                name={field}
                value={formData[field]}
                onChange={handleChange}
                variant="outlined"
                fullWidth
              />
            </Grid>
          ))}
        </Grid>

        <Button
          type="submit"
          variant="contained"
          color="primary"
          sx={{ mt: 3, display: 'block', ml: 'auto', mr: 'auto' }}
        >
          Add Patient
        </Button>
      </form>
    </Box>
  );
};

export default AddPatient;
