import React, { useState } from 'react';
import Sidebar from '../components/Sidebar';
import { Box, TextField, Button, Typography } from '@mui/material';
import axios from 'axios';

const AddPatient = () => {
  const [formData, setFormData] = useState({
    'Patient ID': '',
    'Patient Name': '',
    Age: '',
    'Emergency Type': '',
    'ESI Level': '',
    'Patient Check-In Time': '',
  });

  const [responseMessage, setResponseMessage] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/add-patient', formData);
      setResponseMessage(response.data.message);
      setFormData({
        'Patient ID': '',
        'Patient Name': '',
        Age: '',
        'Emergency Type': '',
        'ESI Level': '',
        'Patient Check-In Time': '',
      });
    } catch (error) {
      console.error('Error adding patient:', error);
      setResponseMessage('Failed to add patient.');
    }
  };

  return (
    <div style={{ display: 'flex' }}>
      <Sidebar />
      <main
        style={{
          flexGrow: 1,
          padding: '20px',
          marginTop: 64,
          boxSizing: 'border-box',
        }}
      >
        <Typography variant="h4" gutterBottom>
          Add New Patient
        </Typography>
        <Box
          component="form"
          onSubmit={handleSubmit}
          sx={{
            display: 'flex',
            flexDirection: 'column',
            gap: 2,
            maxWidth: '400px',
          }}
        >
          <TextField
            label="Patient ID"
            name="Patient ID"
            value={formData['Patient ID']}
            onChange={handleInputChange}
            required
          />
          <TextField
            label="Patient Name"
            name="Patient Name"
            value={formData['Patient Name']}
            onChange={handleInputChange}
            required
          />
          <TextField
            label="Age"
            name="Age"
            type="number"
            value={formData.Age}
            onChange={handleInputChange}
          />
          <TextField
            label="Emergency Type"
            name="Emergency Type"
            value={formData['Emergency Type']}
            onChange={handleInputChange}
          />
          <TextField
            label="ESI Level"
            name="ESI Level"
            type="number"
            value={formData['ESI Level']}
            onChange={handleInputChange}
          />
          <TextField
            label="Check-In Time"
            name="Patient Check-In Time"
            type="datetime-local"
            value={formData['Patient Check-In Time']}
            onChange={handleInputChange}
          />
          <Button type="submit" variant="contained" color="primary">
            Add Patient
          </Button>
        </Box>
        {responseMessage && (
          <Typography variant="body1" sx={{ mt: 2 }}>
            {responseMessage}
          </Typography>
        )}
      </main>
    </div>
  );
};

export default AddPatient;
